    "@modelcontextprotocol/sdk": "^1.15.0",
    "puppeteer": "^24.12.1",
    "superagent": "^10.2.2",
    "zod": "^3.25.75"
  },
  "devDependencies": {
    "@types/node": "^24.0.10",
    "@types/superagent": "^8.1.9",
    "typescript": "^5.8.3"
  }
}
