    "node-fetch": "^3.3.2"
  },
  "devDependencies": {
    "@types/node": "^20.0.0",
    "tsx": "^4.0.0",
    "typescript": "^5.8.3"
  },
  "author": "Lucy Goodchild",
  "license": "MIT"
}
