{
  "name": "mcp-jira-server",
  "version": "1.1.0",
  "description": "Model Context Protocol server for Jira integration with Claude Code",
  "type": "module",
  "main": "dist/index.js",
  "bin": {
    "mcp-jira-server": "dist/index.js"
  },
  "scripts": {
    "build": "tsc",
    "dev": "tsx watch src/index.ts",
    "start": "node dist/index.js",
    "clean": "rm -rf dist",
    "typecheck": "tsc --noEmit",
    "lint": "eslint src --ext .ts",
    "test": "jest"
  },
  "keywords": ["mcp", "jira", "claude", "ai", "integration", "atlassian"],
  "author": "MCP Jira Server Contributors",
  "license": "MIT",
  "repository": {
    "type": "git",
    "url": "https://github.com/tom28881/JIRA_MCP.git"
  },
  "bugs": {
    "url": "https://github.com/tom28881/JIRA_MCP/issues"
  },
  "homepage": "https://github.com/tom28881/JIRA_MCP",
  "dependencies": {
    "@modelcontextprotocol/sdk": "^1.7.0",
    "node-fetch": "^3.3.2",
    "zod": "^3.24.2",
    "zod-to-json-schema": "^3.24.2",
    "dotenv": "^16.4.7"
  },
  "devDependencies": {
    "@types/node": "^20.14.0",
    "@typescript-eslint/eslint-plugin": "^6.21.0",
    "@typescript-eslint/parser": "^6.21.0",
    "eslint": "^8.57.0",
    "jest": "^29.7.0",
    "ts-jest": "^29.1.5",
    "tsx": "^4.15.0",
    "typescript": "^5.4.5"
  },
  "engines": {
    "node": ">=18.0.0"
  }
}
