=== package.json ===
{
  "name": "@kehvinbehvin/json-mcp-filter",
  "version": "1.0.2",
  "repository": {
    "type": "git",
    "url": "https://github.com/kehvinbehvin/json-mcp-filter.git"
  },
  "publishConfig": {
    "registry": "https://npm.pkg.github.com"
  },
  "main": "build/index.js",
  "type": "module",
  "bin": {
    "json-mcp-server": "./build/index.js"
  },
  "scripts": {
    "build": "tsc && chmod 755 build/index.js",
    "start": "node build/index.js",
    "inspect": "npx @modelcontextprotocol/inspector node build/index.js"
  },
  "files": [
    "build",
    "src",
    "README.md"
  ],
  "keywords": [
    "mcp",
    "model-context-protocol",
    "json",
    "schema",
    "typescript",
    "quicktype",
    "json-filter",
    "claude"
  ],
  "author": "Kevin",
  "license": "MIT",
  "description": "MCP server providing JSON schema generation and filtering tools using quicktype",
  "dependencies": {
    "@modelcontextprotocol/sdk": "^1.17.1",
    "quicktype-core": "^23.2.6",
    "zod": "^3.25.76"
  },
  "devDependencies": {
    "@types/node": "^24.1.0",
    "typescript": "^5.9.2"
  }
}
